const item = $input.item;

if (!item.binary || !item.binary.pdf_file) {
  throw new Error("Arquivo PDF nÃ£o encontrado em binary");
}

const base64 = item.binary.pdf_file.data.replace(/\n/g, '');

return [
  {
    json: {
      base64_pdf: base64
    }
  }
];

const responses = $json.responses?.[0]?.responses?.[0]?.fullTextAnnotation?.pages || [];

let textoExtraido = "";

for (const page of responses) {
  for (const block of page.blocks || []) {
    for (const paragraph of block.paragraphs || []) {
      for (const word of paragraph.words || []) {
        const palavra = word.symbols.map(s => s.text).join('');
        textoExtraido += palavra + ' ';
      }
    }
  }
}

textoExtraido = textoExtraido.trim();

return [
  {
    json: {
      lead: {
        mensagem: textoExtraido || 'Nenhuma informaÃ§Ã£o extraÃ­da do PDF.'
      }
    }
  }
];

