const keys = $input.first().json.keys || [];
const results = [];

for (const key of keys) {
  results.push({
    json: {
      key: key,
      numero: key.replace('-tracking', '')
    }
  });
}

return results;

const now = new Date();
const items = $input.all();
const leads = [];

for (const item of items) {
  try {
    const tracking = JSON.parse(item.json.tracking_data);
    const lastInteraction = new Date(tracking.last_interaction);
    const hoursSince = (now - lastInteraction) / (1000 * 60 * 60);

    // SÃ³ processa se stage for 'active' ou 'warm'
    if (tracking.stage !== 'cold' && tracking.stage !== 'qualified') {
      let followUpType = null;

      if (hoursSince >= 72 && tracking.follow_up_count === 2) {
        followUpType = 'mark_cold';
      } else if (hoursSince >= 48 && tracking.follow_up_count === 1) {
        followUpType = 'last_attempt';
      } else if (hoursSince >= 24 && tracking.follow_up_count === 0) {
        followUpType = 'value_add';
      } else if (hoursSince >= 3 && tracking.follow_up_count === 0) {
        followUpType = 'soft_nudge';
      }

      if (followUpType) {
        leads.push({
          json: {
            numero: item.json.numero,
            pushName: tracking.pushName || 'Lead',
            hours_inactive: Math.floor(hoursSince),
            follow_up_count: tracking.follow_up_count,
            follow_up_type: followUpType,
            stage: tracking.stage,
            last_message: tracking.last_message || ''
          }
        });
      }
    }
  } catch (error) {
    console.error('Erro ao processar tracking:', error);
  }
}

return leads;

